import React, {Component} from 'react';
import Head from "next/head";
import {Button, Card, Container, Form} from "semantic-ui-react";
import FixedMenuLayout from "../components/footer";


class register extends Component {


    constructor(props) {
        super(props);
        this.state = {
            loading: false,
            errorMessage: '',
            name: '',
            day: 0,
            month: 0,
            year: 0,
            location: ''
        };
    }


    onSubmit = async event => {
        if (this.state.VIN === '' || this.state.day === '' || this.state.month === '' || this.state.year === '' || this.state.type === '' || this.state.mileage  === '' || this.state.desc === '') {
            this.setState({errorMessage: "Fields can't be empty"});}

        else if (this.state.day > 31 || this.state.day < 1) {
            this.setState({errorMessage: "Invalid day"});
        }
        else if (this.state.month > 12 || this.state.month < 1) {
            this.setState({errorMessage: "Invalid month"});
        }
        else if (this.state.year > 2040 || this.state.year < 2019) {
            this.setState({errorMessage: "Invalid year"});
        }

        else if(this.state.errorMessage === ''){
            event.preventDefault();
            this.setState({accounts: await web3.eth.getAccounts()});
            this.setState({loading: true, errorMessage: ''});
            await instance.methods.addEvent(this.state.VIN, this.state.day, this.state.month, this.state.year, this.state.type, this.state.mileage, this.state.desc).send({
                from: this.state.accounts[0]
            });

        }

    };





    render() {

        let cardStyle = {
            width: '50%',
            margin: '100px auto'

        };

        let labelStyle = {
            margin: '10px',
            textAlign: 'center'
        };

        let formstyle = {
            opacity: '100%'

        };

        let buttonStyle = {
            margin: '4px'
        };

        return (
            <div>

                <div>

                </div>
                <Head>
                    <link
                        rel="stylesheet"
                        href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.11/semantic.min.css"
                    />
                    <style>{`
        body {
         background: url("https://images.unsplash.com/photo-1511649475669-e288648b2339?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2089&q=80");
         background-repeat: no-repeat;
         background-position: center; 
         background-attachment: fixed;
        }
      `}</style>
                </Head>
                <Container> zz</Container>


                <Card color="blue" style={cardStyle}>
                    <label style={labelStyle}><h2>Register</h2></label>
                    <Form className={"form-inline"} onSubmit={this.onSubmit}
                          error={!!this.state.errorMessage} style={formstyle}>
                        <Form.Group inline style={{width: '60%', margin: '30px auto'}}>

                            <Form.Field required style={{width: '70%', margin: '10px'}}>


                                <Form.Input fluid label="Name:"
                                            aria-placeholder={{color: 'purple'}}
                                            style={{width: '100%'}}
                                            value={this.state.name}
                                            onChange={this.handleChangeAddress}
                                            error={!!this.state.errorMessage}
                                />

                                <Form.Input fluid label="Location:"
                                            aria-placeholder={{color: 'purple'}}
                                            style={{width: '96.555%'}}
                                            value={this.state.name}
                                            onChange={this.handleChangeName}
                                            error={!!this.state.errorMessage}
                                />


                                <h4>Date of Birth:</h4>
                                <Form.Group inline widths="equal" style={{width: '21%' }}>
                                    <Form.Field style={{margin: '20%'}}>
                                        <Form.Input label="day:"
                                                    style={{width:'100px'}}
                                                    value={parseInt(this.state.day)}
                                                    onChange={this.handleAddDay}
                                                    type="number"
                                                    min={1} max={31} step={1}
                                        />


                                        <Form.Input label="month:"
                                                    style={{width:'100px'}}
                                                    value={parseInt(this.state.month)}
                                                    onChange={this.handleAddMonth}
                                                    type="number"
                                                    min={1} max={12} step={1}
                                        />


                                        <Form.Input label="year:"
                                                    style={{width:'100px'}}
                                                    value={parseInt(this.state.year)}
                                                    onChange={this.handleAddYear}
                                                    type="number"
                                                    min={2019} max={2040} step={1}
                                        />

                                    </Form.Field>
                                </Form.Group>




                            </Form.Field>

                        </Form.Group>
                        <Form.Field style={{textAlign: 'center', margin: '10%'}}>
                            <Button style={{width:'30%', height:'50px'}}onClick={this.onSubmit} color={"blue"}>
                                Submit
                            </Button>
                        </Form.Field>

                    </Form>

                </Card>

                <FixedMenuLayout/>
            </div>
        );
    }

}

export default register;
