import React, {Component} from 'react';
import Head from "next/head";
import {Button, Card, Container, Form, Message} from "semantic-ui-react";
import FixedMenuLayout from "../components/footer";
import instance from "../components/verifier.js";
import web3 from '../components/web3.js';



class ShowInfo extends Component {


   constructor(props){
       super(props);
       this.state={
           loading: false,
           accounts: [],
         info:'',
       }
   }


    componentDidMount(){
        this.loadBlockchainData()
    }

    async loadBlockchainData() {
        this.setState({accounts: await web3.eth.getAccounts()});
        this.setState({info: await instance.methods.getBasicInfo(this.state.accounts[0]).call()});
    }



    render() {

        let cardStyle = {
            width: '23%',
            margin: '100px auto',


        };

        let labelStyle = {
            margin: '10px',
            textAlign: 'center'
        };

        let formstyle = {
            opacity: '100%'

        };

        return(
            <Card style={cardStyle}>
                <Card.Content>
                    <Card.Header> NAME: </Card.Header>
                    <Card.Description>{this.state.info[0]}</Card.Description>
                    <Card.Header>LOCATION:</Card.Header>
                    <Card.Description>{this.state.info[1]}</Card.Description>
                    <Card.Header>COLOR:</Card.Header>
                    <Card.Description>{this.state.info[2]}</Card.Description>
                    <Card.Header>LICENSE:</Card.Header>
                    <Card.Description>{this.state.info[3]}</Card.Description>

                </Card.Content>
            </Card>
        );
    }
}

export default ShowInfo
