import React, {Component} from 'react';
import Head from "next/head";
import {Button, Card, Container, Form, Message} from "semantic-ui-react";
import FixedMenuLayout from "../components/footer";
import instance from "../components/verifier.js";
import web3 from '../components/web3.js';


class Requests extends Component {

    componentDidMount() {
        this.loadBlockchainData()
    }

    async loadBlockchainData() {
        this.setState({accounts: await web3.eth.getAccounts()});
        this.setState({isCompany: await instance.methods.isCompany(this.state.accounts[0]).call()});
            const y = await instance.methods.getJobRequestCount().call();
            for (let i = 0; i < y; i++) {
                const z = await instance.methods.getJobRequest(i).call();
                this.setState({jobrequests: [...this.state.jobrequests, z]});
            }

        }



    constructor(props) {
        super(props);
        this.state = {
            jobrequests: [],
            certrequests: [],
            isCompany: false,
            isInstitution: false
        }

    }


    renderJobRequest() {
        const x = this.state.accounts[0];
        let r = this.state.jobrequests.map(function (jobrequest) {
            return [<Table.Row>
                <Table.Cell key={x}>{jobrequest[2].toString()}</Table.Cell>
                <Table.Cell key={x}>{jobrequest[0].toString()}</Table.Cell>
                <Table.Cell key={x}>{jobrequest[1].toString()}</Table.Cell>
                <Table.Cell>{y}</Table.Cell>
            </Table.Row>]
        });
        return r;
    }

    renderJobRequestTable() {

        let tableStyle = {
            width: '38%',
            margin: '30px auto'
        };


        if (this.state.jobrequests && this.state.jobrequests.length) {
            return (<Table celled style={tableStyle} color={'blue'} key={'blue'} inverted>

                    <Table.Header>
                        <Segment inverted color="blue" style={{textalign: 'center'}}><h2>Jobs</h2></Segment>
                        <Table.Row>
                            <Table.HeaderCell> SSN</Table.HeaderCell>
                            <Table.HeaderCell>Months Worked</Table.HeaderCell>
                            <Table.HeaderCell>Position</Table.HeaderCell>
                            <Table.HeaderCell>Eth Address</Table.HeaderCell>
                        </Table.Row>
                    </Table.Header>
                    <Table.Body>
                        {this.renderJobRequest()}
                    </Table.Body>
                </Table>
            );
        } else return null;
    }

    render() {
console.log('jobs:',this.state.jobrequests);
        console.log('company:',this.state.isCompany);
        return (
            <div>
                <div></div>

            <Head>
            <link
        rel="stylesheet"
        href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.11/semantic.min.css"
            />
            <style>{`
        body {
         background: url("https://images.unsplash.com/photo-1511649475669-e288648b2339?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2089&q=80");
         background-repeat: no-repeat;
         background-position: center; 
         background-attachment: fixed;
        }
      `}</style>
    </Head>

                <div>{this.renderJobRequestTable()}</div>
            <FixedMenuLayout/>
            </div>
        );



    }
}
    export
    default
    Requests;
