import React, {Component} from 'react';
import Head from "next/head";
import {Button, Card, Container, Form, Message} from "semantic-ui-react";
import FixedMenuLayout from "../components/footer";
import instance from "../components/verifier.js";
import web3 from '../components/web3.js';



class ShowInfo extends Component {


   constructor(props){
       super(props);
       this.state={
           loading: false,
           accounts: [],
         info:[]
       }
   }


    componentDidMount(){
        this.loadBlockchainData()
    }

    async loadBlockchainData() {
        this.setState({accounts: await web3.eth.getAccounts()});
        this.setState({info: await instance.methods.getBasicInfo(this.state.accounts[0]).call()});
    }



    render() {
        return(
<div>x</div>
        );
    }
}

export default ShowInfo
