import React, {Component} from 'react';
import Head from "next/head";
import {Button, Card, Container, Form, Message} from "semantic-ui-react";
import FixedMenuLayout from "../components/footer";
import instance from "../components/verifier.js";
import web3 from '../components/web3.js';



class ShowInfo extends Component {


   constructor(props){
       super(props);
       this.state={
           loading: false,
           accounts: [],
         info:null
   };
}
    componentWillMount(){
        this.loadBlockchainData()
    }

    async loadBlockchainData() {
        this.setState({accounts: await web3.eth.getAccounts()});
        const x = await instance.methods.getBasicInfo(this.state.accounts[0]).call();

    }
    onSubmit = async event => {
        event.preventDefault();
        this.setState({accounts: await web3.eth.getAccounts()});
        try{
        const x = await instance.methods.getBasicInfo(this.state.accounts[0]).call();
        this.setState({
            info: x
        });
        } catch (err) {
            this.setState({errorMessage: err.message});
        }
        return(<Card >
            <Card.Content>
                <Card.Header> NAME: </Card.Header>
                <Card.Description>{this.state.info[0].toString()}</Card.Description>
                <Card.Header>LOCATION:</Card.Header>
                <Card.Description>{this.state.info[1].toString()}</Card.Description>
                <Card.Header>COLOR:</Card.Header>
                <Card.Description>{this.state.info[2].toString()}</Card.Description>
                <Card.Header>LICENSE:</Card.Header>
                <Card.Description>{this.state.info[3].toString()}</Card.Description>

            </Card.Content>

        </Card>);
    };
    render() {
console.log(this.state.info);
        let cardStyle = {
            width: '23%',
            margin: '100px auto',


        };

        let labelStyle = {
            margin: '10px',
            textAlign: 'center'
        };

        let formstyle = {
            opacity: '100%'

        };

        return(
            <div>
                <div>

                </div>
                <Head>
                    <link
                        rel="stylesheet"
                        href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.11/semantic.min.css"
                    />
                    <style>{`
        h1,
        a {
          font-family: 'Arial';
        }

        Card {
           box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)
           }

        body {
         background: url("https://images.unsplash.com/photo-1511649475669-e288648b2339?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2089&q=80");
         background-repeat: no-repeat;
         background-position: center; 
         background-attachment: fixed;
        }
      `}</style>


                </Head>

<Card style={ cardStyle }>
                <Button onClick={this.onSubmit}>B</Button>
</Card>
                <FixedMenuLayout/>
            </div>
        );
    }
}

export default ShowInfo
